sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.table={
		"list":[]
	};

	data.userDisplayName=gs.getUserDisplayName();
	data.userName=gs.getUserName();

	var courseGr = new GlideRecord("sys_user");
	courseGr.addQuery('active',true);
	courseGr.orderBy('name');
	courseGr.query();

	while(courseGr.next()){
		var obj ={};
		obj.name=courseGr.getValue('name');
		obj.email=courseGr.getValue('email');
		data.table.list.push(obj);
	}
//gs.info(JSON.stringify(data.table.list));

})();
